mapper.select:
select count(*) as cnt from tour_reserve where member_id=#{memberId}


		and status=0


		and status=1

select * from (select ROWNUM as rnum, p.* from (select r.reserve_no as reserveNo, r.item_no as itemNo, t.item_title as itemTitle, r.member_id as memberId, m.member_name as memberName, r.tour_date as tourDate, r.tour_time as tourTime, r.person_count as personCount, r.pay_date as payDate, r.total_pay as totalPay, r.status, r.check_review as checkReview from tour_reserve r join member m on(m.member_id=r.member_id) join tour t on(r.item_no=t.item_no) where r.member_id=#{memberId}
and r.status=0
and r.status=1
order by r.reserve_no desc)p) where rnum between #{start} and #{end}

select r.reserve_no as reserveNo, t.item_title as itemTitle, r.member_id as memberId, m.member_name as memberName, m.phone as phone, r.tour_date as tourDate, r.tour_time as tourTime, r.person_count as personCount, r.pay_date as payDate, r.total_pay as totalPay, r.status as status
from tour_reserve r join member m on(m.member_id=r.member_id) join tour t on(t.item_no=r.item_no) 
where t.member_id=#{memberId} 

	and r.item_no=#{itemNo}


	and m.member_name like #{memberName}


	and m.phone like #{phone}


	and r.tour_date=#{tourDate}


	and r.tour_time=#{tourTime}


	and status=0 and sysdate <= r.tour_date


	and status=1


	and status=0 and r.tour_date < sysdate

order by r.tour_date asc, r.reserve_no desc
