mapper.select:
select rownum, fin.* from( select * from( select region_country as regionCountry, rec_no as recNo, rec_title as recTitle, rec_content as recContent, rec_writer as recWriter, read_count as readCount, rec_date as recDate, rec_category as recCategory, region_city as regionCity, place, coords, cnt from recommend_board left join region using(region_no) left join (select count(*) as cnt , board_no from liked where board_class = 2 group by board_no)rlist on (rec_no = board_no)) join photo on (recNo = board_no)
regionCountry = #{country}
and regionCity = #{city}
and recCategory = #{category}
and recTitle like '%'||#{search}||'%'
order by recNo desc)fin where rownum between #{start} and #{end}

mapper.update:
update board_comment
	set comment_content=#{commentContent}
	where board_class=2 and board_no=${boardNo} and comment_no=${commentNo}

mapper.select:
select count(*) from( select region_country as regionCountry, rec_no as recNo, rec_title as recTitle, rec_content as recContent, rec_writer as recWriter, read_count as readCount, rec_date as recDate, rec_category as recCategory, region_city as regionCity, place, coords, cnt from recommend_board left join region using(region_no) left join (select count(*) as cnt , board_no from liked where board_class = 2 group by board_no)rlist on (rec_no = board_no)) join photo on (recNo = board_no)
regionCountry = #{country}
and regionCity = #{city}
and recCategory = #{category}
and recTitle like '%'||#{search}||'%'
order by recNo desc

mapper.update:
update recommend_board set
	rec_title = #{recTitle}, rec_content = #{recContent},
	rec_category = #{recCategory}, region_no = #{regionNo},
	coords = #{coords}, place = #{place}
	where rec_no = #{recNo}
