mapper.select:
select trip_board_no as tripBoardNo, trip_board_title as tripBoardTitle, trip_board_content as tripBoardContent, trip_route as tripRoute, trip_board_writer as tripBoardWriter, to_char(trip_board_date,'yyyy. mm. dd.') as tripBoardDate, to_char(trip_board_date,'hh24:mi') as tripBoardTime, read_count as readCount, region_no as regionNo, filepath, region_country||'-'||region_city as regionName, (select count(member_id) from liked where board_class = 5 and board_no = #{tripBoardNo}) as likeCount
, (select member_id from liked where board_class = 5 and board_no = #{tripBoardNo} and member_id = #{memberId}) as likedId, (select bookmark_no from bookmark where board_class = 5 and board_no = #{tripBoardNo} and member_id = #{memberId}) as bookmarkNo
from trip_board left join (select * from photo where board_class = 5) on(trip_board_no = board_no) join region using(region_no) where trip_board_no = #{tripBoardNo}

mapper.update:
update trip_board set
	trip_board_title = #{tripBoardTitle},
	trip_board_content = #{tripBoardContent},
	trip_route = #{tripRoute},
	region_no = #{regionNo}
where trip_board_no = #{tripBoardNo}

mapper.select:
select * from ( select rownum as rnum, trip_board_no as tripBoardNo, trip_board_title as tripBoardTitle, trip_board_content as tripBoardContent, trip_route as tripRoute, trip_board_writer as tripBoardWriter, to_char(trip_board_date,'yyyy. mm. dd.') as tripBoardDate, to_char(trip_board_date,'hh24:mi') as tripBoardTime, read_count as readCount, region_no as regionNo, filepath from( select * from trip_board left join (select * from photo where board_class = 5) on(trip_board_no = board_no)
join region using(region_no)
order by trip_board_no desc )
where region_country like '%'||#{keyword}||'%' or region_city like '%'||#{keyword}||'%'
where trip_board_title like '%'||#{keyword}||'%'
) where rnum between #{start} and #{end}
